<div class="b2bPayment">
    <h3>
        <img id="ddql132r4EVM4M:" src="data:image/png;base64," jsaction="load:str.tbn" class="rg_ic rg_i" alt="Image result for b2b pay logo" style="width: 225px; height: 225px; margin-left: 19px; margin-right: 19px; margin-top: -26px;" data-atf="1">
    </h3>
    <div class="content">
        <button class="btn button #ff26bb ladda-button btn-outline btn-circle b2bPaymentStart"
                data-spinner-color=#ff26bb
                type="button"
                data-style="contract">
            <i class="fa fa-money"></i>Pay now
        </button>
        <div class="b2bPaymentErrors" style="display: none"></div>
    </div>
</div>

<script type="text/javascript">
    
    $('.b2bPaymentStart').click(function () {
        var options = {
            actionUrl: '{{ scopeRoute("payment.handler") }}',
            successCallBack: function (response) {
                Ladda.stopAll();
                if (response['result']['next'] == 'pending') {
                    //window.location.replace(response['result']['redirectUri']);
                    $('#payment_result').html(response['result']['content']);
                    $('.prompt-link[data-target="pay-address"]').attr('type','button');
                    $('.visible-xs-block').closest('.col-sm-9').removeClass('.col-sm-9');
                    $('.hidden-xs').remove();
                    $('.visible-xs-block').remove();
                    $('#tab5').addClass('active');
                    $('#tab4').removeClass('active');
                    $('.proceed').attr('href',response['result']['url']);
                    $('.proceed').show();
                }
            },
            failCallBack: function (response) {

            }
        };
        sendForm(options);

    });

    $(document).on('click','.prompt-link[data-target="pay-address"]',function(){
        var copyText = $('.pay-address')[0];
        var selection = window.getSelection();
        var range = document.createRange();
        range.selectNodeContents(copyText);
        selection.removeAllRanges();
        selection.addRange(range);
        /* Copy the text inside the text field */
        document.execCommand("copy");

        /* Alert the copied text */
        alert("Copied the text: " + copyText.innerHTML);
    })
</script>

<style>
    .b2bPayment button {
        width: auto !important;
        font-weight: 600;
        padding: 3px 10px !important;
        margin: 25px auto;
        display: block;
    }

    .b2bPaymentErrors {
        text-align: center;
        max-width: 500px;
        margin: auto;
        font-size: 12px;
        border-radius: 5px !important;
        background: #4F91FF;
        color: #4F91FF;
    }

    .b2bPayment button img {
        width: 27px;
        margin-right: 5px;
        color: #0FD6C5;
    }

    .b2bPayment h3 img {
        max-width: 100px;
        margin: auto;
        display: block;
    }

    .b2bPayment h3 {
        border-bottom: 1px solid #7a7a7a;
        padding-bottom: 7px;
    }
</style>